css:
 body {
     margin: 0;
     padding: 0;
}
 .print-format td, .print-format th {
     padding: 5px !important;
}
 .border-none {
     border-top: none !important;
}
 .h5 {
     margin-top: 1px !important;
     margin-bottom: 1px !important;
}
 .address table {
     width: 100%;
     border-collapse: collapse;
}
 .address td {
     width: 33%;
     padding: 10px;
     vertical-align: top;
}
 img {
     max-width: 100%;
     height: auto;
}
 .si-details {
     border: 1px solid black;
    /* Only apply border to the outer container */
}
 .si-details table {
     border: none;
    /* Remove border from inner tables */
}
 .si-details tr, .si-details td {
     padding: 5px;
    /* Optional padding */
}
 .so-items th {
     border: 1px solid black;
     border-collapse: collapse;
}
 .so-items td {
     border-left: 1px solid black;
     border-right: 1px solid black;
     border-collapse: collapse;
}
 .total-row td {
     border: 1px solid black;
     border-collapse: collapse;
}
 .so-items th{
     background-color:#E5FBE0;
     color:black;
     font-weight:bold;
     text-align:center;
}
 body:last-child .print-format td img {
    width: auto !important;
}
