html:
{% set items_per_page = 24 %}
{% set total_items = doc.items | length %}
{% set pages = (total_items // items_per_page) + (1 if total_items % items_per_page > 0 else 0) %}

{% for page_num in range(pages) %}

<!-- LETTERHEAD -->
<div {% if print_settings.repeat_header_footer %} id="header-html" class="hidden-pdf" {% endif %}>
    {% if letter_head and not no_letterhead %}
    <div class="letter-head">{{ letter_head }}</div>
    {% endif %}
</div>

<!-- MAIN -->
<main>
    <div class="item-details">
        <table class="item-table">
            <tbody>
                {% for item in doc.items[page_num * items_per_page : (page_num + 1) * items_per_page] %}
                <td class="text-center" style="width:37.8 px;">
            {{item.idx}}
        </td>
        <td class="text-center" style="width:41.7px;">
            Main
        </td>
        <td  style="width:274.3px;">
            {{item.description}}
        </td>
        <td class="text-center" style="width:52.8px;">
            {{item.uom}}
        </td>
        <td class="text-center" style="width:52.8px;">
            {{item.qty}}
        </td>
        <td class="text-center" style="width:56.4px;">
            {{item.rate}}
        </td>
        <td class="text-center" style="width:49.2 px;">
            {{item.tax_rate}}%
        </td>
        <td class="text-center" style="width:52.8px;">
            {{item.tax_amount}} 
        </td>
        <td class="text-center" style="width:86.6 px;">
            {{item.amount}}
        </td>
    </tr>
                {% endfor %}

                {% if page_num == pages - 1 %}
                <!-- Totals only on the last page -->
                <tr>
                    <td colspan="3" style="text-align: right;">Sub Total</td>
                    <td colspan="5"></td>
                    <td style="border-top: 1px solid black; text-align: center;">
                        {{ doc.get_formatted("total") }}
                    </td>
                </tr>
                <tr>
                    <td colspan="3" style="text-align: right;">VAT</td>
                    <td colspan="5"></td>
                    <td style="border-top: 1px solid black; text-align: center;">
                        {{ doc.get_formatted("total_taxes_and_charges") }}
                    </td>
                </tr>
                {% endif %}
            </tbody>
        </table>
    </div>

    {% if page_num == pages - 1 %}
    <!-- FOOTER: Displayed only on the last page -->
    <div id="footer-html" style="height: 5.5cm; margin-top: 20px;">
        <div class="footer" style="position: relative; bottom: -8cm; width: 100%;">
            {% if footer %}
            {{ footer }}
            {% endif %}
        </div>
    </div>
    {% endif %}
</main>

{% if page_num < pages - 1 %}
<!-- Page break after each page except the last one -->
<div style="page-break-after: always;"></div>
{% endif %}

{% endfor %}

<style>
.address-table {
    margin-top: 184px;
    width: 100%;
}
.address-table td {
    line-height: 15px !important;
}
.item-table {
    width: 100%;
    font-size: 12px !important;
    margin-top: 34.9px;
}
.item-table .text-center {
    text-align: center;
}
.item-table .text-right {
    text-align: right;
}
.item-table .text-left {
    text-align: left;
}
table {
    font-size: 12px !important;
}
.print-format td,
.print-format th {
    padding: 0.5px !important;
}
.print-format {
    padding: 1px !important;
}
@media print {
    .print-format {
        padding: -20px !important;
    }
}
</style>
