    margin-left:24px !important;
    margin-right:5px !important;
}
.address-table {
    margin-top:3.37cm !important;
    width: 100%;
    /*border: 1px solid black;*/
}
.address-table td {
    line-height: 15px !important;
}
.si-item-table {
    width: 100%;
    font-size: 12px !important;
    margin-top: 34.9px;
    /*border: 1px solid black;*/
}
.si-item-table .text-center {
    text-align: center !important;
}
.si-item-table .text-right {
    text-align: right;
}
.si-item-table .text-left {
    text-align: left;
}

.si-item-table td{
    /*border: 1px solid black;*/
}
table {
    font-size: 12px !important;
}
